std::vector<std::vector<int>> harderEasyProblem(std::vector<long long> p, std::vector<int> q, std::vector<int> r)
{
	std::vector<std::vector<int>> rs;
	
	rs.resize(q.size());

	for (int i = 0; i < rs.size(); i++)
	{
		// Find rs[i][0] and rs[i][1]
		// We know that:
		// rs[i][0] * rs[i][1] = p[i]
		// rs[i][0] / rs[i][1] = q[i] 	
		// rs[i][0] % rs[i][1] = r[i]
		// rs[i][0] = q[i] * rs[i][1] + r[i]
		// So we have quadratic equation: 
		// (q[i] * rs[i][1] + r[i]) * rs[i][1] = p[i]
		// q[i] * rs[i][1]^2 + rs[i][1]*r[i] - p[i] = 0
		
			rs[i].resize(2);
			
			if (q[i] == 0)
			{
				rs[i][0] = r[i];
				rs[i][1] = p[i] / r[i];
			}

			//else if (q[i] == 1)
			//{
			//	// q[i] == 1 then rs[i][0] - rs[i][1] = r[i]
			//	// we know rs[i][0] * rs[i][1] = p[i]
			//	//long long r2 = (long long)r[i] * (long long)r[i];
			//	//long long sq = r[i] * r[i] + (4 * p[i]);
			//	long long deta = std::sqrtl((long long )r[i] * (long long)r[i] + 4 * p[i]);
			//	rs[i][1] = ( deta - r[i])/2;
			//	rs[i][0] = rs[i][1] + r[i];
			//}

			else
			{

				// find delta
				long long delta = (long long )r[i] * (long long)r[i] + 4 * q[i] * p[i];

				long long canDelta = std::sqrtl(delta);
				
				rs[i][1] = (-r[i] - std::sqrtl(delta)) / (2 * q[i]);
				if (rs[i][1] < 0)
				{
					rs[i][1] = (-r[i] + std::sqrtl(delta)) / (2 * q[i]);
				}


				rs[i][0] = p[i] / rs[i][1];

			}
	
		
	}

	return rs;
}

std::string stringTask2(std::string str)
{
	int len = str.length();
	std::string result = "";

	for (int i = 0; i < len; i++)
	{
		if (str[i] == 'a'
			|| str[i] == 'A'
			|| str[i] == 'o'
			|| str[i] == 'O'
			|| str[i] == 'y'
			|| str[i] == 'Y'
			|| str[i] == 'e'
			|| str[i] == 'E'
			|| str[i] == 'u'
			|| str[i] == 'U'
			|| str[i] == 'i'
			|| str[i] == 'I'
			|| str[i] < 'A'
			|| str[i] > 'z'
			|| (str[i] > 'Z' && str[i] < 'a')
			)
		{
			continue;
		}
		if (str[i] < 'a')
		{
			result = result + '.' + (char)((int)str[i] + 32);
		}
		else
		{
			result = result + '.' + str[i];
		}
	}

	return result;
}

